INSERT INTO `about_us_alternatebusinessmodelsection` (`id`, `header`, `image`, `paragraph`, `language`) VALUES
	(1, 'दूर की दृष्टि', 'about_us/images/vision-min.jpg', '<p>इस बढ़ती उपभोक्ता अर्थव्यस्था के समाज में छोटे दुकानदारों को प्रगितिशील बनाना हमारा लक्ष्य है।<br />\r\nयह कार्य हम अपने 100%&nbsp;डिजिटल प्लेटफार्म के ज़रिये करेंगे और व्यापारियों तक आसान और पर्याप्त पूंजी&nbsp;पहुंचाएंगे। ब्रिज2कैपिटल का यह सदैव उद्देश्य रहेगा की छोटे व्यवसायों को इस उपभोक्ता युक्त युग में, आसानी से और सामर्थ्य अनुसार,&nbsp;हमारे ऑनलाइन मंच द्वारा क्रेडिट प्रदान कर उन्हें सफल बनाएं।</p>', 'Hindi'),
	(2, 'दूरंदेशी कार्यकर्म', 'about_us/images/mission-min.jpg', '<p>अगले पांच सालों में 50,000 छोटे व्यवसायों को लिए एक पूंजी पूल का बुनियादी ढांचा नियुक्त करना।<br />\r\nविश्वसनीय संचालन और भविष्य-योगी तकनीक&nbsp;के साथ हम भारत सरकार के डिजिटल उपक्रम जैसे &ndash; इंडिया स्टैक्स, भीम, आधार, जीएसटी का उचित इस्तेमाल कर, छोटे कारोबारों को डिजिटल अर्थव्यवस्था में शामिल करेंगे।</p>', 'Hindi'),
	(3, 'उद्देश्य', 'about_us/images/about-us-objective-min.jpg', '<p>उभरते बाज़ारों के कस्बों एवं शहरों में स्थित लाखों छोटे कारोबारों को ब्रिज2कैपिटल प्रदान कर, उन क्षेत्रों का स्थानीय जीडीपी बढ़ाएंगे, तथा छोटे व्यवसायों को अव्यवस्थित क्षेत्र से उठा व्यवस्थित वातावरण में जोड़ेंगे।</p>\r\n\r\n<p>ब्रिज2कैपिटल - एक फिनटेक प्लेटफार्म जो विश्वास और तकनीक&nbsp;का सकुशल मेल है।</p>', 'Hindi'),
	(4, 'Vision', 'about_us/images/vision-min_9y4PKOA.jpg', '<p>To enable millions of small businesses to become competitive in a growing consumption economy. We plan to do this by improving access to sufficient, affordable capital through a 100% digital platform. </p>', 'English'),
	(5, 'Mission', 'about_us/images/mission-min_lhrKeDh.jpg', '<p>We aim to accomplish this through our strengths of entrepreneurship, trust, technological skills, and management by our future-perfect technology intervention leveraging upon the Government of India&rsquo;s Digital India initiatives, such as &ndash; India Stack, BHIM, Aadhaar, and GST.</p>', 'English'),
	(6, 'Objective', 'about_us/images/about-us-objective-min_Z6Wi7FE.jpg', '<p>To accelerate local GDP by providing Bridge2Capital to millions of small businesses in small towns and cities of emerging markets, thus lifting them from an unorganized sector into an organized environment. </p>\r\n \r\n <p>Bridge2Capital is a Fintech platform that seamlessly combines trust with technology.</p>', 'English');
