INSERT INTO `about_us_greenbordersection` (`id`, `header`, `paragraph`, `language`) VALUES
	(1, 'A never-before quick and easy way to get business loans and maximize profits - aaiye sales badhaiye!', 'Bridge2Capital is a mobile app that helps small business owners improve their financial health which helps them get adequate and on time credit to increase their sales. This helps them enhance their profits within a short period of 2-3 months - from the convenience of their shops.', 'English'),
	(3, 'आइये सेल्स बढ़ाइये : एक नए, तीव्र और सहज तरीके से लोन पाइये और मुनाफा कमाइए', 'ब्रिज2कैपिटल एक मोबाइल ऐप है जिसके सहारे छोटे दुकानदार व व्यापारी अपनी दुकान से ही लोन लेकर, बिक्री बढ़ा कर, अपना मुनाफा सिर्फ 2-3 महीनों में डबल कर सकते हैं। ब्रिज2कैपिटल यह मुमकिन करता है की सीमित लघु लोन पाकर, दुकानदार अपने दुकान के लिए सामान की खरीददारी करे - वो भी सिर्फ मिनटों में।', 'Hindi');
