INSERT INTO `about_us_mainbannertwobutton` (`id`, `header`, `paragraph1`, `paragraph2`, `paragraph3`, `button1_text`, `button2_text`, `button1_url`, `button2_url`, `image`, `language`) VALUES
	(1, 'About Bridge2Capital - Loans for MSME', 'From small towns to big cities of India, Bridge2Capital eases the business of MSMEs, every day.', 'Every day, a tired small business owner gets the Bridge2Capital support to begin fresh.', 'Every day, a Bridge2Capital customer opens up a shop filled with supplies, smiles and success.', 'FOR SMALL BUSINESS OWNERS', 'FOR INVESTORS', 'https://uat-website.bridge2capital.com/credit-product/', 'https://bridge2capital.com/investors/', 'about_us/images/about-banner-min_QTlhgzK.jpg', 'English'),
	(2, 'व्यापार में बदलाव, जिंदिगी में सुधार', 'प्रतिदिन ब्रिज2कैपिटल, देश के कस्बों व बड़े शहरों के छोटे दुकानदारों और व्यापारियों का कारोबार आसान बनाता है', 'प्रतिदिन थके हुए दुकानदारों को ब्रिज2कैपिटल का सहारा नयी शुरुआत करने में मदद करता है', 'प्रतिदिन ब्रिज2कैपिटल का दुकानदार अपनी दुकान को सामान, मुस्कान, और इत्मीनान के साथ खोलता है', 'दुकानदार के लिए', 'निवेशक के लिए', '/hi/credit-product/', '/hi/investors/', 'about_us/images/about-banner.jpg', 'Hindi');
