INSERT INTO `about_us_ourstory` (`id`, `header`, `paragraph1`, `paragraph2`, `image`, `person1_name`, `person1_designation`, `person2_name`, `person2_designation`, `language`, `person3_designation`, `person3_name`) VALUES
	(1, 'Our Story', 'Like many Indians, we too were continuing with our safe 9-5 job. Life was comfortable. We went to office, returned home, spent time with family, watched TV, then got ready for the next day.', 'We were working in business insurance at the time. Our work took us to small towns and cities across India, where we met countless small business owners and storekeepers. What struck us was that each one of them had a similar story to tell: the one of having to close shops due to a lack of funds. Seeing them shut down not just their shops, but also their dreams had a huge impact on us. At the back of our minds a thought lingered: how can unorganised small businesses be in tune with organised selling?', 'about_us/images/Founder_Photo.jpeg', 'Mohammed Riaz', 'Founder', 'Gopi Krishna', 'Co-Founder', 'English', '', ''),
	(2, 'हमारी कहानी', 'सब की तरह, हम भी अपनी सुरक्षित 9-5 की नौकरी कर रहे थे। ज़िन्दगी आरामदायक थी। हम दफ़्तर जाते, घर आते, परिवार के साथ टीवी देखते, फिर दुसरे दिन के लिए तैयार होते।', 'उस दौरान, हम लघु अर्थव्यवस्था में काम कर रहे थे। हमारा कार्य हमें भारत के कईं कस्बों और शहरों में लेकर गया जहाँ हमारी मुलाकात कईं छोटे दुकानदारों और व्यापारियों से हुई। हमने एक समान बात देखी: पूंजी न होने के कारण दुकानदार अपने दुकान बंद कर रहे थे। उनके दुकानों को ही नहीं बल्कि उनके सपनों को भी बंद होते देख, हम अत्यंत प्रभावित हुए। मन में सिर्फ एक बात टटोलती थी, किस तरह छोटे व अव्यवस्थित व्यवसायों को व्यवस्थित विक्रेता बनाया जाये ?', 'about_us/images/Founder.jpeg', 'Mohammed Riaz', 'Founder', 'Gopi Krishna', 'Co-Founder', 'Hindi', 'Co-Founder', 'Nitin Verma');
