INSERT INTO `homepage_section2` (`id`, `heading_1`, `heading_2`, `heading_3`, `url`, `btn_text`, `language`) VALUES
	(1, 'Easy Financial Health Check for Indian Small Business owners - Bridge2Capital, a product of Xtracap Fintech India Pvt Ltd, is an app that provides the ability to increase the creditworthiness of small business owners to be able to access business loans- digitally, transparently & easily. Thousands of small business owners have made their businesses easier and have grown exponentially by using our app. Would you like to join our fast-growing community of DIGITAL DUKANDAARS? Then, install our easy-to-use app to improve the financial health of your business and increase profits manifold.', '', '', 'https://play.google.com/store/apps/details?id=com.bridge2capital&hl=en', 'INSTALL NOW', 'English'),
	(2, 'ब्रिज2कैपिटल, एकस्ट्राकैप इंडिया फिनटेक प्राइवेट लिमिटेड का, एक प्रगतिशील ऐप है। यह ऐप विशेषकर भारत के छोटे दुकानदारों को आसानी से कम समय में लघु अवधि लोन देने के लिए बनाया गया है।  हज़ारों दुकानदारों ने दावा किया है की ब्रिज2कैपिटल कि मदद से उनके व्यापार में काफी उन्नति हुई है। क्या आप भी ब्रिज2कैपिटल के साथ इस नए, डिजटल ज़माने के डिजिटल दुकानदार बन्ना चाहेंगे ? आप भी अपने व्यापर में डबल मुनाफा कमा सकते हैं – आसानी से, सुरक्षा से और तीव्रता से।  कैसे ?  हम अपने ऐप के ज़रिये छोटे दुकानों और व्यापारों को लघु अवधि लोन दिलाते हैं। आप ब्रिज2कैपिटल ऐप को गूगल प्ले स्टोर से डाउनलोड कर सकते हैं। ऐप खोलने के बाद, आपको हमारे साथ रजिस्टर करना होगा। फिर शीघ्र करावाई के पश्चात, कुछ ही घंटों में आपके लिए आपका क्रेडिट लिमिट खाता बैंक में खोल दिया जायेगा। खाता खुलने पर आपको सीमित लोन दीजाएगी जिससे आप अपने जीएसटी रजिस्टर्ड सप्लायरों से माल खरीद सकते हैं।', '', '', 'https://play.google.com/store/apps/details?id=com.bridge2capital&hl=en', 'इंस्टॉल करें', 'Hindi');
