INSERT INTO `investors_ourapproach` (`id`, `header`, `paragraph`, `language`) VALUES
	(1, 'Our Approach', '<p>Our approach is to provide a 360-degree financial health check to small business owners and recommend a suitable working capital limit for supply chain finance (invoice finance). We first pay the bills on behalf of the small business owners to their suppliers, then the small business owners can conveniently repay us afterward. Our business runs on the strength of trust, knowledge, skillful management, and high-level technology.</p>\r\n \r\n <p>Most small business owners&rsquo; under leverage the advantage of smartphones due to unfamiliarity with technology. Our online support helps and assists them in self-help methods of technology use.</p>\r\n \r\n <p>Bridge2Capital is further enabled by the digital initiatives of the Government of India such as GST, UPI, and Aadhaar.</p>', 'English'),
	(2, 'हमारा दृष्टिकोण', '<p>ब्रिज2कैपिटल का बिज़नेस तंत्र,&nbsp;विक्रेता पूंजी प्रभंध (सप्लाई चैन फाइनेंस)&nbsp;&lsquo;रिवर्स फैक्टरिंग&lsquo;&nbsp;पर आधारित है। पहले हम दुकानदारों के तरफ से उनके सप्लायरों का बिल पेमेंट करते हैं। उसके पश्चात,&nbsp;दुकानदार हमें वह रकम आसानी से चुकाते हैं।&nbsp;&nbsp;</p>\r\n\r\n<p>हमारा बिज़नेस विशवास,&nbsp;ज्ञान,&nbsp;कुशल संचालन एवं उच्चस्तरीय तकनीक&nbsp;पर आधारित है।&nbsp;&nbsp;</p>\r\n\r\n<p>अधिकांश छोटे व्यापारी डिजिटल तकनीक&nbsp;से अज्ञात होने के कारण अपने स्मार्टफोन का पूरा लाभ नहीं उठाते। इसलिए हमारी ऑनलाइन सहयता और स्थानीय प्रतिनिधि निश्चित करते हैं की सही दुकानदार आसानी से हमारे ऐप को समझ,&nbsp;हमारे साथ बेफिक्र जुड़ जाएं।&nbsp;&nbsp;</p>\r\n\r\n<p>हमारे ऐप &ndash; ब्रिज2कैपिटल &ndash; को अत्यधिक प्रबल व सफल बनाता है भारत सरकार की डिजिटल उपक्रम;&nbsp;जैसे जीएसटी , यूपीआई , आधार।&nbsp;</p>', 'Hindi');
