INSERT INTO `news_news` (`id`, `title`, `by_tag`, `description`, `body`, `image`, `url`, `alt_tag`, `created_at`, `updated_at`, `date`, `featured`, `slug`, `meta_tags`, `meta_description`, `language`) VALUES
	(1, 'The second charge of startups driving the ‘Financial Inclusion’ agenda in the country', 'By Team YourStory', 'The 9 fintechs selected for the second cohort of the Financial Inclusion Lab are gearing to scale technology-based solutions for small businesses, MSMEs, farmers, low-literate individuals and chit funds while continuing to focus on low and middle income and underserved populations.', '<div class="container" style="margin-top:30px; max-width:1280px; padding-left:15px; padding-right:15px; text-align:left; width:1280px">\r\n<div class="col-sm-10" style="max-width:83.3333%; padding-bottom:0px; padding-left:15px; padding-right:15px; width:1041.66px">\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><em>The 9 fintechs selected for the second cohort of the Financial Inclusion Lab are gearing to scale technology-based solutions for small businesses, MSMEs, farmers, low-literate individuals and chit funds while continuing to focus on low and middle income and underserved populations.</em></span></span></span></span></p>\r\n\r\n<h2 style="text-align:left"><span style="font-size:32px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>About Financal Inclusion Lab Cohort 2: &nbsp;VALIDATE</strong></span></span></span></strong></span></h2>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The Financial Inclusion Lab, a part of IIMA-CIIE&rsquo;s Bharat Inclusion Initiative (BII), is a collaboration between BII, J.P. Morgan, Michael &amp; Susan Dell Foundation, Bill &amp; Melinda Gates Foundation and global consulting firm MSC, to help scale early-stage fintech startups that address the unique needs of lower- and middle-income people. The US$9.5 million initiative will provide the start-ups with the necessary support, capital, strategic guidance, market access and mentorship to build and test their products in twelve months.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Members of the 2nd cohort of the Financial Inclusion Lab After the success of the first cohort this second group of the Lab is designed to help post-MVP stage startups to develop and validate sustainable go-to-market and scale-up strategies.</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Startups:</strong></span></span></span></strong></span></h3>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>ChitMonks</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Over 30,000 registered chit funds have been playing a stellar role in the financial inclusion of India. They are easy to understand and participate for the low and middle income (LMI) segments. However, lack of trust and operational challenges in administering the funds are plaguing the industry. ChitMonks&rsquo; blockchain based solution works with state registrars to solve some of these issues.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">It helps registered chit fund business digitize their operations and compliance requirements. Taking an ecosystem approach, it also works with banks and other stakeholders to make chit fund operations more efficient, trustworthy and transparent, which ultimately makes chit funds safer and more rewarding for their members.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: Pavan Adipuram, Malla Reddy Ch, Sridhar, Veera Praveen</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>GUVI</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Over 7.5 lakh engineering students graduate every year from 3500+ colleges of India. More than 80 percent, especially those from rural areas, are not employable due to inadequate skills, lack of exposure and guidance. Guvi is an online platform that enables people to build relevant and in-demand technical skills through proprietary gamified content in vernacular languages to reduce friction in learning and make it meaningful.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Guvi employs a full stack &lsquo;phygital&rsquo; approach involving learning, mentoring, assessment and placement services. It ties up with IT product companies looking to hire technical talent that helps its users find a job, irrespective of their physical location.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: SP Balamurugan, Arun Prakash M, Sridevi M</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>KyePot</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Currently ~500 million low-and-middle income individuals of Tier 2 and 3 cities are relying on local instruments such as chits, committees, and money lenders for their savings, borrowing and investment needs, which are unsafe, prone to leakages and unreliable.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">KyePot was started with an aim to digitise chit funds for users to promote it as an asset class in annual household financial planning. KyePot is India&rsquo;s first and only end-to-end digital platform built from the ground up for group savings and borrowing. It is run with full regulatory compliance where users get the safety &amp; security of their funds and can also operate in the comfort of their current environment 24x7 with their friends, family, social contacts and with trusted individuals who have been verified by KyePot.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: Sidd Gandhi, Dr.&nbsp;Kiran Lakhotia</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Bridge2Capital</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Bridge2Capital empowers small shopkeepers with a simple and easy-to-use three click mobile application, which assists them in accessing on-demand affordable working capital credit from formal financial institutions. The UVP of the solution is its cash flow based short-term working capital assistance linked to the GSTN data of a shopkeeper. The model is shopkeeper-centric, enabling the store owners to deal with suppliers of their choice.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The business processes are designed to ensure rapid disbursement, controlled end utilisation of the loan, and positive repayment behaviour which results in a steady increase in the available limits for the shopkeeper. Over time, Bridge2Capital looks to help India&rsquo;s 14+ million shopkeepers trust technology to increase the efficiency of their business and move from unorganised to organised business behaviour.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: Mohammed Riaz, Nishant Singh</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Navana Tech</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Navana Tech empowers businesses to build user-friendly digital interfaces for the next billion. The founders observed that the current smartphone operating systems, apps and services are not designed for the 300 million+ low-literate Indians because of the heavy reliance on text and widespread use of western iconography.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Navana Tech is building a design language and voice technology stack in local languages for businesses and independent developers to create understandable interfaces for their low literate customers. This includes domain-specific speech-to-text in Indian languages, a contextual voice assistant to guide users through complex interfaces, a repository of illustrative iconography, and plug &amp; play user flows for common functionality like sign-in, make a payment, check out and others. The startup believes that simplifying interfaces will encourage low-literate users to independently learn to use and trust new technology.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: Raoul Nanavati, Jai Nanavati</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>PayAgri</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Financial institutions struggle to lend to India&rsquo;s 102+ million small and marginal farmers due to non-availability of reliable data of their cash-based transactions. This results in a shortage of liquidity for farmers during the pre-harvest period leading to increased cost of capital and further distress. PayAgri is building a cashless ecosystem in the agri-value chain by enabling farmers to transact without middlemen on its platform and capture real-time transaction data to build financial profiles for the farmers.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">PayAgri&rsquo;s model provides forward and backward linkages to farmers by aggregating orders through Farmer Producer Organizations to enable bulk transactions with institutional buyers and agri input companies. The startup believes that this model will optimize returns and create value for every player in the value chain by leveraging technology and agri expertise.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: KVM Rajkumar, Rajeev G. Kaimal</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Riskcovry</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Riskcovry is re-imagining Insurance for India&rsquo;s increasingly digital-friendly MSMEs. Despite owning a variety of insurable assets like auto, home, and business, there is no single insurance solution that speaks to these 64+ million MSMEs uniquely, across channels. The problem is further aggravated with lack of personalization that results in poor customer experience.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The startup brings together an Insurance-Distribution-Data-Tech platform, to partner with Financial and Digital businesses like Consumer Internet and FinTechs to solve for unique insurance use cases associated with their MSME customers via a combination of API-delivered sachet/modular insurance products, smart algorithms and intuitive distribution SaaS technology. Riskcovry differentiates itself through its simplified workflow to discover unique risks, provide a personalized cover, and offer recover services with digital first claims intimation and policy management tools.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: Suvendu Prustry, Sorabh Bhandari, Vidya Sridharan, Chiranth Patil</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>DESPITE</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">MIMO acts as a catalyst for established enterprises to expand into semi-urban and rural India. It provides a temporary workforce in the form of agents who perform last mile delivery, collections and other micro-tasks for large enterprises such as financial institutions, government, marketing, distribution companies and other organizations. MIMO has been up-skilling and creating employment for youth in rural areas across 10 states in India. Most of the field officers are primary school dropouts, residing in their hometown and are given initial training after being on-boarded.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The startup believes that the low-educated and unemployed can be financially included only if they earn digitally with a dignified job and can work around the lack of education. By engaging the local communities, enterprises are able to solve several issues at one time and in addition provide customized services/ products to people.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founder: Lathika Regunathan</span></span></span></span></p>\r\n\r\n<h3 style="text-align:left"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>RupeeCircle</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">There are more than 525 million underbanked and unbanked individuals in India. RupeeCircle is helping the underbanked population, especially those without a credit score, to access credit through formal channels. The startup is a Peer-to-Peer (P2P) lending marketplace that connects creditworthy borrowers with investors looking for attractive returns on their investments.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">RupeeCircle&rsquo;s proprietary credit scoring model uses new age technology and a variety of alternative data points to effectively assess borrower&rsquo;s credibility even when they lack traditional credit history. The platform completely manages the complete loan cycle on behalf of the investors (screening, verification, underwriting, collections) and in doing so it offers a new investment asset class for them to diversify their portfolios beyond conventional stocks and bonds.</span></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founders: Ajit Kumar, Abhishek Gandhi, Ashish Mehta, Piyush Saurabh</span></span></span></span></p>\r\n</div>\r\n</div>', 'news/images/Screenshot_5_Kv6JwnT.png', 'https://yourstory.com/2019/04/financal-inclusion-lab-cohort-2?utm_pageloadtype=scroll', 'Bridge2Capital', '2021-02-23 12:12:40.572337', '2021-02-23 12:12:40.572374', '2019-04-25', 1, 'financial-inclusion-lab-cohort', '', '', 'English'),
	(2, 'Xtracap Fintech raises $1 million in angel funding round', 'By ETtech', 'Xtracap Fintech India has raised $1 million (Rs 7.56 crore) in an angel round of funding, the Gurugram-based startup announced on Wednesday.', '<p>Xtracap Fintech India has raised $1 million (Rs 7.56 crore) in an angel round of funding, the Gurugram-based startup announced on Wednesday.<br />\r\n<br />\r\nWhile the three-year-old company did not disclose names, it said that the capital had been raised from a clutch of US and UK-based investors. Xtracap Fintech will use the proceeds to increase product offerings on its app, strengthen its IT infrastructure and increase its distribution capability.<br />\r\n<br />\r\nThe startup, which was founded by Mohammed Riaz&nbsp;and Nishant Singh in May 2017, also plans to launch additional digital solutions, including, payments, order management, logistics, insurance and financial services, along with an accounting platform, by March 2021, it said in a prepared statement.&nbsp;</p>\r\n\r\n<p>Xtracap Fintech owns and operates an invoice financing platform Bridge2Capital, which helps retailers in small towns and cities connect with banks and NBFCs to access short-term liquidity. It offers loans in the Rs 50,000-Rs 5 lakh range, with a credit period of 45 days and a daily repayment schedule.<br />\r\n<br />\r\n&ldquo;We aim to provide a digital ecosystem in about 1,500-plus towns and cities with high consumer demand. With increase in digital adoption, this time period will shrink in the future due to&nbsp;the unprecedented Covid-19 situation,&rdquo; Riaz, founder and chief executive of Xtracap Fintech, said.</p>', 'news/images/Screenshot_13.png', 'https://tech.economictimes.indiatimes.com/news/startups/xtracap-fintech-raises-1-million-in-angel-funding-round/76030636', 'Bridge2Capital', '2021-02-23 12:23:48.164137', '2021-02-23 12:23:48.164170', '2020-05-27', 0, 'xtracap-fintech-raises-1-million-in-angel-funding-round', '', '', 'English'),
	(3, 'Angel investors infuse $1 million in fintech firm Xtracap', 'By Shashank Didmishe', 'Gurugram-based Xtracap Fintech India, which owns and operates an invoice financing platform Bridge2Capital, has raised $1 million in an angel round from undisclosed investors.', '<p>Gurugram-based Xtracap Fintech India, which owns and operates an invoice financing platform Bridge2Capital, has raised $1 million in an angel round from undisclosed investors.&nbsp;</p>\r\n\r\n<p>The company will increase the product offerings on its application and expand to new geographies using the fresh funds, a statement said.&nbsp;</p>\r\n\r\n<p>Launched in October 2018 by Delhi School of Economics alumnus Mohammed Riaz, the platform&rsquo;s mainstay business is to help small businesses get loans to purchase goods. For example, a retail shopkeeper can place an order with a wholesaler using the app, while a financier will settle the retailer&rsquo;s dues to the wholesaler based on the invoice.&nbsp;</p>\r\n\r\n<p>It offers loans in the Rs 50,000-Rs 5 lakh range, with a credit period of 45 days and a daily repayment schedule. Bridge2Capital claims that it has managed invoice financing of Rs 70 crore for customers in Rajasthan, Punjab and Telangana.</p>\r\n\r\n<p>The company, which has seen 30,000 registration since inception, looks to onboard 20 lakh retailers by 2028, the statement said. It plans to launch additional digital solutions, such as payments, order management, logistics, insurance and financial services, along with an accounting platform, by March 2021, it added.</p>\r\n\r\n<p>At the time of inception, Xtracap raised funds from CIIE.CO-managed Bharat Inclusion Initiative. It was also part of the latter&rsquo;s&nbsp;<a href="https://www.techcircle.in/2018/10/25/meet-the-11-fin-tech-startups-picked-for-the-first-batch-of-financial-inclusion-lab" rel="nofollow" target="_blank">first batch of Financial Inclusion Lab</a>. &nbsp;</p>\r\n\r\n<p>The company operates in the same space as Tiger Global-backed invoice discounting app KredX. In 2019, KredX raised $26 million in a Series B funding round led by the New York-based alternative investments firm.</p>', 'news/images/Screenshot_8.png', 'https://www.techcircle.in/2020/05/22/angel-investors-infuse-1-million-in-fintech-firm-xtracap', 'Bridge2Capital', '2021-02-23 12:26:08.828825', '2021-02-23 12:26:08.828874', '2020-05-22', 0, 'angel-investors-infuse-1-million-in-fintech-firm-xtracap', '', '', 'English'),
	(4, '[Funding alert] Xtracap Fintech raises $1 million in angel funding', 'By Team YS', 'Xtracap Fintech, the Gurugram-based startup, has raised $1 million in angel round of funding. The startup had launched its mobile app – Bridge2Capital in October 2018 for small business owners to receive limited loan amount to pay their GST supplier bills.', '<p>Xtracap Fintech, the Gurugram-based startup, has raised $1 million in angel round of funding. The startup had launched its mobile app &ndash; Bridge2Capital in October 2018 for small shopkeepers to receive limited loan amount to pay their GST supplier bills.</p>\r\n\r\n<p>According to this startup, since launch of the app, it has seen an overall customer increase from less than hundred to over 1,000 shopkeepers. It has crossed over 30,000 registrations across India.</p>\r\n\r\n<p>Xtracap said it has managed Rs 70 crore worth of invoice financing providing credible financial integrity to small shopkeepers, under its limited geographical operations, in Rajasthan, Punjab, and Telangana.</p>\r\n\r\n<h4>The startup claimed that despite coronavirus outbreak, it has registered stable revenue generation. Xtracap&rsquo;s app allows shopkeepers to choose their GST suppliers and releases funds to the suppliers for the invoices financed.</h4>\r\n\r\n<p>The startup plans to use this funding to offer new products and penetrate into newer markets.</p>\r\n\r\n<p>Besides Bridge2Capital, Xtracap Fintech also has other offerings like QR based online payment solution, order management logistic payment solution, online insurance, digital gold saving scheme and digital ledger. The startup expects its customer base to have access to all these services by March 2021.</p>\r\n\r\n<p>Xtracap was also part of the incubation programme under Bharat Financial Inclusion managed by CIIE&ndash;IIMA. It also received certain grant funding from CIIE&ndash;IIMA.</p>\r\n\r\n<h4>The startup aims to target about two million retailers in a serviceable market of 14 million retailers covering 1,500 plus locations.</h4>\r\n\r\n<p>&ldquo;With increase in digital adoption, this time period will shrink in the future due to the unprecedented COVID-19 situation,&rdquo; said Xtracap founder Mohammed Riaz.</p>\r\n\r\n<p>The startup also felt that given the background of mega investment deals announced by Reliance Jio from strategic investors such as Facebook, Silverlake Partners, General Atlantic and Vista Equity, the pace of digital adoption is set to increase in the country.</p>', 'news/images/Screenshot_7.png', 'https://yourstory.com/2020/05/funding-alert-xtracap-fintech-raises-1-million', 'Bridge2Capital', '2021-02-23 12:34:39.294589', '2021-02-23 12:34:39.294625', '2020-05-20', 0, 'funding-alert-xtracap-fintech-raises-1-million', '', '', 'English'),
	(5, 'India FinTech Forum unveils 18 high potential fintech startups for IFTA 2019', 'By FPJ Bureau', 'India FinTech Forum, a not for profit fintech advocacy group representing Indian fintech companies, have announced 18 fintech startups who will be giving live demos to a high profile jury next month to compete for the Fintech Startup of the Year award at India FinTech Awards (IFTA) 2019 taking place on 29th Nov 2019 in Mumbai.', '<p>India FinTech Forum, a not for profit fintech advocacy group representing Indian fintech companies, have announced 18 fintech startups who will be giving live demos to a high profile jury next month to compete for the Fintech Startup of the Year award at&nbsp;<a href="https://indiafintech.com/" target="_blank">India FinTech Awards (IFTA) 2019&nbsp;</a>taking place on 29<sup>th</sup>&nbsp;Nov 2019 in Mumbai.</p>\r\n\r\n<p>After diligent and careful evaluation, Knowledge Partner The Digital Fifth has narrowed down the top 18 FinTech startups that will be pitching on November 29, 2019, at The Lalit, Mumbai.</p>\r\n\r\n<p>The winning startup will be awarded the prestigious IFTA Fintech Startup of the year award and will get an opportunity to participate in Paris Fintech Forum 2020.</p>\r\n\r\n<p>In the past editions, winners included well-known names like Capital Float, ZestMoney and Oz Forensics. Past winners of IFTA have cumulatively raised over a billion USD in funding from VCs till date.</p>\r\n\r\n<p>&ldquo;Being identified as the &lsquo;Fintech Startup of the year&rsquo; during the FinTech awards and subsequently getting a direct entry to Paris Fintech Forum was a very significant milestone in our startup journey. This was one of the first major awards we received and it was a great recognition of the hard work we had put into building our product and platform.&rdquo; says&nbsp;<strong>Lizzie Chapman, Co-Founder &amp; CEO, ZestMoney</strong>&nbsp;&amp; IFTA Fintech Startup of the year 2017.</p>\r\n\r\n<p>The India FinTech Forum is a non-profit initiative that offers a platform for fintech companies to collaborate and voice opinion on the relevant policy issues. Indian fintech firms have unique opportunities with the India Stack (Aadhar, UPI, etc.), which are not available anywhere else in the world. The India FinTech Forum organizes meet-ups in several locations and seeks to drive policies that accelerate the growth of fintech in India.</p>\r\n\r\n<p>The process of shortlisting was more quantitative than qualitative to ensure an objective decision by the panel. IFTA Knowledge Partner helped score each registered startup across several parameters. Additionally, feedback was sought from other event partners, startup accelerators and experts from the fintech sector. After careful deliberation on numerous strong applications, 18 firms have been shortlisted to present at IFTA.</p>\r\n\r\n<p>&ldquo;The depth and width of Fintechs in India have improved exponentially in the last couple of years. We had received around 400 interesting startup applications in the event, out of which we have selected 18 to demonstrate their innovation in front of 800+ attendees. We can see a new trend emerging wherein Indian Fintechs are now getting ready for entering international markets as well as foreign startups are now making inroads into India&rdquo;, says&nbsp;<strong>Sameer Singh Jaini, Convener, India FinTech Forum&rsquo;s IFTA 2019 and CEO, The Digital Fifth</strong>.</p>\r\n\r\n<p>This year&rsquo;s event is potentially the most impactful one in India with multiple curated tracks running through the day along with business workshops and Investor matchmaking sessions. We hope that this event would help in accelerating the Indian Fintech Ecosystem,&rdquo; he adds.</p>\r\n\r\n<p><strong>IFTA is proud to present the shortlisted 18 FinTech startups for 2019:</strong></p>\r\n\r\n<p>1) Active.Ai (Singapore) - Active.Ai is a Conversational AI platform built specifically for BFSI with deep capabilities of enabling Transactions, Service Requests, Enquiries and FAQs.</p>\r\n\r\n<p>2) Bon Credit (India) - Bon Credit is a digital lending start-up that provides micro-credit for the gig economy that tends towards temporary contractors and freelancers instead of full-time employees.</p>\r\n\r\n<p>3) CreditMate (India) - CreditMate platform provides 100% data secured, real time collections performance for lenders and our Machine Learning algorithm improves collection resolution rates at all stages of delinquency.</p>\r\n\r\n<p>4) CreditOn (India) - CreditOn enables large entrenched lenders to transform themselves into nimble digital lenders at scale consistent while preserving portfolio quality.</p>\r\n\r\n<p>5) CredoLab (Singapore) - CredoLab uses smartphone device data to generate a behavioural score for the customer that predicts her probability of defaulting.</p>\r\n\r\n<p>6) Fintuple Technologies (India) -Fintuple is India&rsquo;s fastest growing investor portfolio management platform.</p>\r\n\r\n<p>7) Jai Kisan (India) - Jai Kisan is a fintech platform catering to rural emerging markets, providing risk adjusted return to lenders and a low cost of capital for the borrower through subvention, securitization, an asset light model to lower distribution costs to create technologically enabled channel partner outlets with existing rural stakeholders brands and next generation credit assessment.</p>\r\n\r\n<p>8) Lokyata (USA) - Lokyata provides AI/ML- driven customer analytics for social impact lenders to better understand their customer base, provision scores across multiple dimensions of the customer profile, and engage the customers using AI/ML transparency technology that provisions &quot;decision codes&quot; used to generate personalized messages.</p>\r\n\r\n<p>9) Neener Analytics (France) - Neener Analytics is the leader in computational social science and the only social media analytics technology that delivers specific, individual, risk outcomes in a 100% friction-less environment.</p>\r\n\r\n<p>10) Partnerhub Ltd (Hungary) &ndash; The company provides its customers own branded invoice &ndash; related services for their SME customers which enables them to issue, process and pay their e-invoices with just a couple of clicks which saves time &amp; money.</p>\r\n\r\n<p>11) Rewire (Israel) &ndash; Rewire is an international remittance platform for migrant workers, who usually transfer most of their income to their countries of origin.</p>\r\n\r\n<p>12) Riskcovry InsurTech Platform (India) - AI enabled health insurance claim APIs, which can be integrated into a natural messaging experience like WhatsApp. Policy holders or agents can now submit health insurance claims in under 5 minutes, with Riskcovry AI being able to enrich the experience by guiding the user through a personalized, simple and secure process.</p>\r\n\r\n<p>13) Think Anlaytics (India) - Think Analytics is an advanced analytics and technology start up with considerable experience in building AI &amp; ML frameworks and solutions. Their product Algo360 is comprehensive data underwriting solution for &lsquo;new to credit&rsquo; segment.</p>\r\n\r\n<p>14) Valocity Global (New Zealand) - Valocity transforms end-to-end valuation process for banks. The platform connects the entire valuation ecosystem through a smart cloud based platform that digitises the workflow for seamless automation while capturing data from end to end to enable address validation (including a Valuers&rsquo; App), valuation benchmarking and analytics, and essentially a faster mortgage valuation process that removes collusion and enables global best practice regulatory compliance.</p>\r\n\r\n<p>15) Vested Finance (USA) - Vested simplifies US stock investing for investors based in India. Users can now invest in companies they know and use, such as Amazon, Google, Starbucks and more in a commission-free and in an end-to-end online process via this platform.</p>\r\n\r\n<p>16) Vymo (USA) - Vymo is an intelligent Personal Sales Assistant; it captures sales activities automatically and predicts &lsquo;next best actions&rsquo; intelligently. Vymo has 100,000 users in 50+ large enterprises such as AXA, Allianz, Generali, and Sumitomo. Vymo is a Gartner Cool Vendor and funded by Emergence Capital and Sequoia Capital.</p>\r\n\r\n<p>17) Xtracap Fintech India (India) - Bridge2capital is a product built on the principle of reverse Factor. The app is the first app in India, which provides funds to Small business for the purchase of their bills from GST registered suppliers.</p>\r\n\r\n<p>18) Yobee Research (India) &ndash; Yobee Research uses AI &amp; Data Science to generate high probability &amp; actionable trading signals. Signals are generated by multiple neural network for top 500 traded companies (representing 99.97% of daily trading volume). These signals are combined to produce one signal per company. Signals that qualify by having a minimum probability threshold will be delivered to customers for his final action.</p>', 'news/images/freepressjournal_2019-10_b7c5c5d1-40b0-4976-9476-a0785c3897e2_india_fintech.webp', 'https://www.freepressjournal.in/business/event-corner/india-fintech-forum-unveils-18-high-potential-fintech-startups-for-ifta-2019', 'Bridge2Capital', '2021-02-23 12:37:26.279488', '2021-02-23 12:37:26.279521', '2019-10-30', 0, 'the-india-fintech-forum-unveils-18-high-potential-fintech-startups-for-ifta-2019', '', '', 'English'),
	(6, 'IFTA selects 18 FinTech startups; winner to participate in Paris FinTech Forum 2020', 'By ET BFSI Team', 'India FinTech Forum has announced 18 FinTech startups who will be giving live demos to a high profile jury next month to compete for the FinTech Startup of the Year award at India FinTech Awards (IFTA) 2019, to be hosted in Mumbai on 29th Nov at the Lalit hotel.', '<p>India&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/news/fintech">FinTech</a>&nbsp;Forum has announced 18&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/tag/fintech">FinTech</a>&nbsp;startups who will be giving live demos to a high profile jury next month to compete for the&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/tag/fintech+startup+of+the+year">FinTech Startup of the Year</a>&nbsp;award at&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/tag/india+fintech+awards">India FinTech Awards</a>&nbsp;(IFTA) 2019, to be hosted in Mumbai on 29th Nov at the Lalit hotel.<br />\r\n<br />\r\nThe not-for-profit FinTech advocacy group that represents Indian FinTech companies has shortlisted &mdash; Active.Ai (Singapore); Bon Credit (India);&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/tag/creditmate">CreditMate</a>&nbsp;(India); CreditOn (India); CredoLab (Singapore); Fintuple Technologies (India); Jai Kisan (India); Lokyata (USA); Neener Analytics (France); Partnerhub Ltd (Hungary); Rewire (Israel); Riskcovry InsurTech Platform (India); Think Anlaytics(India); Valocity Global (Australia); Vested Finance (USA); Vymo (USA); Xtracap Fintech India (India); and Yobee Research (India).<br />\r\n<br />\r\n&ldquo;This was one of the first major awards we received and it was a great recognition of the hard work we had put into building our product and platform,&rdquo; said Lizzie Chapman, Co-Founder &amp; CEO, ZestMoney &amp; IFTA FinTech&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/tag/startup+of+the+year">Startup of the year</a>&nbsp;2017.<br />\r\nThe Digital Fifth, the knowledge partner, was given the task to narrow down the top 18 FinTech startups that will be pitching on November 29, 2019.<br />\r\n<br />\r\n&ldquo;The depth and width of FinTechs in India have improved exponentially in the last couple of years. We had received around 400 interesting startup applications in the event, out of which we have selected 18 to demonstrate their innovation in front of 800+ attendees. We can see a new trend emerging wherein Indian FinTechs are now getting ready for entering international markets as well as foreign startups are now making inroads into India,&rdquo; said Sameer Singh Jaini, Convener,&nbsp;<a href="https://bfsi.economictimes.indiatimes.com/tag/india+fintech+forum">India FinTech Forum</a>&rsquo;s IFTA 2019 and CEO, The Digital Fifth.<br />\r\n<br />\r\nThe winning startup will be awarded the prestigious IFTA FinTech Startup of the year award and will get an opportunity to participate in Paris FinTech Forum 2020.<br />\r\n<br />\r\nIn the past editions, winners included well-known names like Capital Float, ZestMoney and Oz Forensics. Past winners of IFTA have cumulatively raised over a billion USD in funding from VCs till date.</p>', 'news/images/Screenshot_9.png', 'https://bfsi.economictimes.indiatimes.com/news/fintech/india-fintech-forum-unveils-18-high-potential-fintech-startups-for-ifta-2019/71714554', 'Bridge2Capital', '2021-02-23 12:39:34.704855', '2021-02-23 12:39:34.704891', '2019-10-23', 0, 'india-fintech-forum-unveils-18-high-potential-fintech-startups-for-ifta-2019', '', '', 'English'),
	(7, 'How fintech startups are helping businesses, investors and the underserved', 'By Team Mint', 'Here are four fintech startups selected by Mint that gave presentations at the Mint Fintech Summit 2019 in Bengaluru.', '<p>Here are four fintech startups selected by Mint that gave presentations at the Mint Fintech Summit 2019 in Bengaluru. Edited excerpts:</p>\r\n\r\n<p><strong>Providing technology payments solutions</strong></p>\r\n\r\n<p>Razorpay is a Bengaluru-based payment solutions company, founded by Shashank Kumar and Harshil Mathur. Established in 2014, the company provides tech payment solutions to more than 350,000 businesses in India. The platform aims to solve basic payment problems faced by the underserved markets by offering a full-stack service including a payment gateway, subscription-based payments, UPI payments, payment links, invoicing and payment collection.</p>\r\n\r\n<p>The company recently introduced Razorpay Capital, its new lending arm, and Razorpay X, a neo-banking platform. It claims to have grown by 5X in terms of transactions in the last year and their investors include Tiger Global, Matrix Partners, Y Combinator and MasterCard.</p>\r\n\r\n<p><strong>Digital lending now becomes easy</strong></p>\r\n\r\n<p>Zestmoney, a Bengaluru-based digital lending startup founded by Lizzie Chapman, Priya Sharma, and Ashish Anantharaman, provides loans to the underserved segment and users without a credit score by underwriting borrowers on alternative credit scoring models.</p>\r\n\r\n<p>Zest money offers a pre-approved credit limit to consumers through a digital platform that can be used to shop online and in physical stores across Zest money partners such as Amazon, Flipkart, Myntra, MakeMyTrip, and Xiaomi. Zestmoney has more than 5.5 million consumers and more than 1,000 merchant partners, such as Flipkart, Amazon, MakeMyTrip, and Mi.</p>\r\n\r\n<p><strong>Offering better model to support small biz</strong></p>\r\n\r\n<p>Xtracap Fintech India, a Delhi-based startup founded by Mohammed Riaz, aims to strengthen the purchasing power of small businesses in India by providing them loans specifically focused on working capital.</p>\r\n\r\n<p>The platform provides working capital for small retail businesses through a transformational business model that ensures appropriate loan utilization. &ldquo;The loans taken by small merchants are often used for personal reasons and their cash crunch remains even after borrowing money,&quot; said Riaz, chief executive officer, Xtracap Fintech India. &ldquo;This led us to develop a better model in providing capital to these merchants&quot; he said. The company has 600 customers who have onboarded 1,200 suppliers in the past eight months.</p>\r\n\r\n<p><strong>Simplifying the MF investments</strong></p>\r\n\r\n<p>Founded in April 2017 by ex-Flipkart employees Lalit Keshre, Harsh Jain, Neeraj Singh, and Ishan Bansal, Groww provides an online platform to invest directly into mutual funds.</p>\r\n\r\n<p>Bengaluru-based Groww allows users to have direct access to all mutual fund products offered by companies including SBI, Reliance, ICICI, and another top mutual fund. The company has also raised over $7 million so far from investors such as Sequoia, Kairos, and Y Combinator. &ldquo;There are 200 million people with investable income in India, but only 20 million investors. The only way to bring the next 180 million onboard is by making investing simple&rsquo;&rsquo; said Jain.</p>\r\n\r\n<p>&nbsp;</p>', 'news/images/Screenshot_11.png', 'https://www.livemint.com/companies/start-ups/how-fintech-startups-are-helping-businesses-investors-and-the-underserved-1562864200603.html', 'Bridge2Capital', '2021-02-23 12:41:53.718037', '2021-02-23 12:41:53.718069', '2019-07-11', 0, 'how-fintech-startups-are-helping-businesses-investors-and-the-underserved', '', '', 'English'),
	(8, 'These 11 fintechs are working to make financial inclusion accessible and affordable for Bharat', 'By Sindhu MV', 'The accelerator programme by Bharat Inclusion selected these 11 fintech startups for developing innovative solutions to democratise access to improve Bharat\'s financial health.', '<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><em>The accelerator programme by Bharat Inclusion selected these 11 fintech&nbsp;startups for developing innovative solutions to democratise access&nbsp;to improve Bharat&#39;s financial health.</em></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The Financial Inclusion Lab, the accelerator programme by the Bharat Inclusion Initiative, has selected 11 fintech startups for its inaugural cohort.&nbsp;The Lab focuses on incubating and accelerating early-stage startups developing technologies for the benefit of underserved communities of &#39;Bharat&#39; in the areas of financial inclusion, skilling and livelihood.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The startups selected for the first cohort are developing innovative solutions to democratise access to credit, information and technology, facilitate easy digital transactions easier, make savings simpler, smarter and flexible and thereby improve the financial health of &lsquo;Bharat.&rsquo;</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Here are the 11 early-stage startups working on realising the dream of financial inclusion for &#39;Bharat&#39; to drive healthy economic growth.</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>WonderLend Hubs</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founded by Ram Ramdas, Anusha Jathanna and Rajesh Iyer, serial entrepreneurs and industry leaders with 80 years of cumulative experience across the banking, financial and software sectors,&nbsp;<strong>WonderLend offers a credit gateway solution to enable zero-friction credit assessment</strong>&nbsp;of the rurban and new-to-credit population, through its B2B2C digital lending hub platform.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The two-year-old Mumbai-based startup believes that one of the key reasons why the underserved have not been able to get easy access to tailored credit products is because &lsquo;for the banking and financial institutions credit assessment of this particular segment has been challenging with low machine decisions, high underwriting and high credit operations costs, making it unviable at scale.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Through a unique combination of its configuration-driven platform,<strong>&nbsp;&lsquo;pay as you go&rsquo; managed service model and augmented credit intelligence solutions</strong>, WonderLend Hubs aims to become the credit gateway for old and new age lenders, fintech players, aggregators and marketplaces, enabling end-to-end friction-free processing of consumer and small business loans from origination to disbursal.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Navana Tech</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">A team of graduates from Cornell Tech and the University of Michigan, Raoul Nanavati and Jai Nanavati have come together to empower low-literates with user-friendly digital interfaces to improve their quality of life. The founders observed that the&nbsp;<strong>current smartphone operating systems, apps and services are not designed for the low-literate Indians</strong>&nbsp;with their heavy reliance on text and widespread use of western iconography.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Navana Tech is creating local image-based design standards and voice-assisted developer tools to make digital services accessible to those with low levels of literacy. After receiving investment from Cornell Tech, they moved to Bengaluru in July 2018. The startup believes that simplifying interfaces will encourage low-literate users to independently learn new technology, enabling them to access the same information, content and services that literate users have access to.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>EasyPlan</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Founded by two Harvard graduates, Manisha Pandita and Sanjay Gandhi, EasyPlan is a&nbsp;<strong>mobile app that helps young Indians save money in a simpler, smarter and more flexible way</strong>. Today, 40 percent of urban Indians below the age of 30 say they are unable to save any money, while 60 percent say they don&rsquo;t have savings to cover them in case of emergencies.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">EasyPlan is addressing this challenge by&nbsp;<strong>helping users automatically save money from their paychecks</strong>. Unlike conventional savings products like FDs and SIPs, EasyPlan is super flexible. Users can reduce the savings amount in one click or skip it without penalties. In case of an emergency, users can also instantly withdraw savings, again without any penalties.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Finlok</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">By bringing communities together onto a digital platform, promoting savings and enabling them to borrow from each other,&nbsp;<strong>Finlok aims to digitise informal group-based financial models</strong>. Started by Tanuj Sinha and Atish Poddar, the 18-month-old startup believes that people earning a monthly income in the range of Rs 8,000 to 25,000 save less and have limited access to formal financial services, thus are exposed to predatory credit or risky investment offerings.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">&ldquo;By bringing the underbanked community together to save,&nbsp;<strong>building a corpus fund, and enabling them to even borrow from each other in addition to borrowing from Finlok</strong>, we are encouraging discipline towards saving while negating the need for credit. Our credit model linked to group savings is one-of-its-kind. It will tremendously benefit the blue-collared workers and people starting careers in the IT and ITeS sector, among others,&rdquo; say the founders.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Kaarva</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Kaarva is on a mission to enable people access to their hard-earned salary when they need it, rather than waiting for it for 30 days. With Kaarva, one can get access to their already earned salary, on demand, with no fees, no interest or hidden charges. Users voluntarily contribute what they like, and receive their money in their bank account instantly.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The founding team, Khushboo Maheshwari and Agam Goyal, are graduates from Harvard Business School and IIT-Bombay. They have diverse experience with companies like InMobi, BlackBuck, P&amp;G, Nike, Booz and have also built their own startups previously.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>ExpressMOJO</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The startup claims that 40 percent of the nine million strong transport trucks in India lie idle at any given point of time due to lack of return loads or finance. And over 70 percent of these trucks are owned and operated by individuals or small fleet owners. The startup is working on creating a technology platform which improves access to information and credit to help these small fleet owners and single truck owners grow their businesses.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">At the moment it has created an online classifieds site where fleet owners and driver-cum-owners can create business profiles, choose to work with verified transporters and commission agents and find available loads in the market. Co-founders, Akshay Gautam and Dipankar Sarkar, say &ldquo;Through ExpressMOJO, we are democratising access to information and credit.&rdquo; The team at ExpressMOJO consists of entrepreneurs and graduates from IIT Delhi and IIM Ahmedabad, with prior experience in logistics and banking.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>GramCover</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">With lack of awareness about insurance and its need among rural customers which is further compounded by complex product features and claim settlement processes, there is low penetration of insurance products at the ground level. GramCover is addressing this challenge by co-creating simple, affordable, and easy-to-understand insurance products and using a technology platform to deliver these products seamlessly to rural customers.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">By working with village level entrepreneurs and providing them with the right mix of technology, products and training, GramCover aims to provide simple and affordable insurance products to rural customers. GramCover started operations in December 2017, after receiving its license from IRDAI. The founding team consists of Jatin Singh, an alumnus of Boston University and Rishabh Garg, an alumnus of IIT Roorkee and IIM Lucknow.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>munshiG</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The startup aims to empower kirana stores with Artificial Intelligence to enable them to manage inventory and finances more efficiently.&nbsp;Some of the available tech solutions are text input-intensive, which makes it cumbersome for adoption by small kirana store owners due to the long learning curve. munshiG overcomes this challenge by making their AI solution &lsquo;voice-based&rsquo;.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">This AI-powered book-keeping tool for kirana stores harnesses Natural Language Processing (NLP) to capture information and makes billing hassle-free. The platform also enables the vendors to send SMSes of bills to the consumers and thereby brings in transparency and cuts down paper usage. By integrating these and many other features into an Android application, they are bringing a low-cost solution to the unorganised retail industry. The startup was co-founded by Nishant Sharma and Ishan Mohammed in April 2018 in Udaipur.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>SureClaim</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Even with the health insurance market in India standing at $3.2 billion and growing at a CAGR of 30 percent and a plethora of health insurance schemes run by state and central governments, various sources estimate that out-of-pocket medical expenses still make up 62 percent of all healthcare costs in India. This has been attributed largely to the limited understanding of health insurance policy benefits and restrictions among customers and the complex claims mechanism.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">SureClaim aims to address this market gap through its intelligent and comprehensive claims platform which enables patients to check their eligibility for treatment coverage in an easy-to-understand language, matches a claimant&rsquo;s expenses with policy coverage and automates the entire claim process for them on a single platform. As a result, the platform ensures maximum payment out of policy to the claimant and reduces their out-of-pocket expense.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">All this is made possible without the claimant needing any technical competency to deal with the insurer. The startup also plans to make claims processing simpler for claimants in other verticals of insurance as well. The Bengaluru-based startup has been founded by Varun Kansal and Anuj Jindal, who formerly worked with Practo.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Jai kisan</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">A team of graduates from Texas A&amp;M University with prior work experience in private equity, venture capital and management consulting have come together to help small farmers get access to affordable credit. Jai Kisan&rsquo;s fintech platform utilises a hyperlocal credit scoring assessment and an innovative securitisation solution that helps financial institutions (banks and NBFCs) make a more accurate assessment of farmers, thereby enabling lower cost of financing to farmers and filling a substantial financing gap in the agriculture sector.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Jai Kisan was founded by Arjun Ahluwalia and Adriel Maniego, graduates of Texas A&amp;M University.&nbsp;The team comes with diverse work experience across private equity, venture capital, restructuring and management consulting.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Xtracap</strong></span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Xtracap provides retailers in small towns and cities an easy and simple mobile application &lsquo;Bridge2capital&rsquo; to connect with banks and NBFCs to access short-term liquidity. According to founders Mohammed Riaz and Nishant Singh, &ldquo;The UVP of our model is the simplicity of our product, ease in product delivery and the trust generated in all the stakeholders on account of transparency.&quot;</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">They added, &quot;Our model is designed to be an aggregation model, wherein we create value for all stakeholders by encouraging them to come together on our platform. In turn, this will establish a trusted and efficient local digital ecosystem which will be facilitated by digital financial transactions.&rdquo;</span></span></span></span></p>\r\n\r\n<h3 style="text-align:justify"><span style="font-size:24px"><strong><span style="font-family:&quot;Fira Sans&quot;,sans-serif"><span style="color:#212529"><span style="background-color:#ffffff"><strong>Making inclusion possible</strong></span></span></span></strong></span></h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">Launched in August this year, the Bharat Inclusion Initiative (BII) from IIM Ahmedabad&#39;s Centre for Innovation Incubation and Entrepreneurship (CIIE) helps tech entrepreneurs across the pre-incubation, seed and scale-up stage. This includes access to research fellowships, sprints, incubators, accelerators, seed funds and similar support programmes to create an end-to-end enabling ecosystem to bring inclusive, for-profit businesses to life. The programme is supported by the Bill &amp; Melinda Gates Foundation, J. P. Morgan, Michael &amp; Susan Dell Foundation and the Omidyar Network.</span></span></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:16px"><span style="font-family:AvenirLTStd-Book"><span style="color:#212529"><span style="background-color:#ffffff">The Financial Inclusion Lab, a key component of&nbsp;Bharat Inclusion Initiative component, offers customised and differentiated support worth over $70,000, which includes prototype capital of up to $30,000, mentoring by distinguished practitioners and sector experts, opportunities to partner with financial and other institutions to build and test products, curated sessions on essential business skills by faculty members and experts from IIM- Ahmedabad, access to scale-up capital via the Bharat Inclusion Seed Fund, cloud credits, legal, marketing, and other ancillary services.&nbsp;MicroSave, an international financial inclusion consultancy firm, will also provide startups with high-touch consulting and insights into the LMI segments.</span></span></span></span></p>', 'news/images/Screenshot_12.png', 'https://yourstory.com/2018/12/fintech-startups-financial-inclusion-accessible-affordable-bharat?utm_pageloadtype=scroll', 'Bridge2Capital', '2021-02-23 12:45:03.409070', '2021-02-23 12:45:03.409121', '2018-12-04', 0, 'fintech-startups-financial-inclusion-accessible-affordable-bharat', '', '', 'English'),
	(9, 'Meet The 11 Fintech Startups From Financial Inclusion Lab', 'By Shreya Ganguly (Inc42 Staff)', 'The $9.5 Mn-worth initiative will support the selected startups for 12 months with capital, strategic guidance, and market access to help them build and test their products.', '<p><em>The Financial Inclusion Lab, a part of IIMA-CIIE&rsquo;s Bharat Inclusion Initiative (BII), has selected 11 fintech innovators for its first intake to the lab, where they will receive mentorship and guidance.</em></p>\r\n\r\n<p><em>The $9.5 Mn-worth initiative will support the selected startups for 12 months with capital, strategic guidance, and market access to help them build and test their products.</em></p>\r\n\r\n<h2><strong>The 11 Fintech Startups Selected By Financial Inclusion Lab</strong></h2>\r\n\r\n<p><strong><a href="https://easyplan.in/" rel="noopener" target="_blank">Easyplan</a>&nbsp;-</strong>&nbsp;Headquartered in Mumbai, Easyplan was founded in 2017 by&nbsp;<a href="https://www.linkedin.com/in/sanjay-gandhi-58318410/" rel="noopener" target="_blank">Sanjay Gandhi</a>&nbsp;and&nbsp;<a href="https://www.linkedin.com/in/manisha-pandita-58281617/" rel="noopener" target="_blank">Manisha Pandita</a>. It has built an app for the millennials that provides a separate a goal-based savings bank account aimed at assisting them to save money through simple and effective ways.</p>\r\n\r\n<p><strong><a href="https://www.expressmojo.com/" rel="noopener" target="_blank">ExpressMojo</a></strong>&nbsp;-&nbsp;Delhi-based ExpressMojo was founded in 2016 by&nbsp;<a href="https://www.linkedin.com/in/akshaygautam1/" rel="noopener" target="_blank">Akshay Gautam</a>&nbsp;and Dipankar Sarkar. The startup has built a platform where small fleet owners and individual drivers can connect directly with the customers.</p>\r\n\r\n<p><strong><a href="http://finlok.com/" rel="noopener" target="_blank">Finlok</a></strong>&nbsp;-&nbsp;Established in 2017, the financial platform Finlok connects people on social network where they can also borrow money from their friends and community while providing people with ways to save money. The platform was founded by&nbsp;<a href="https://www.linkedin.com/in/sinhatanuj/">Tanuj Sinha</a>&nbsp;and&nbsp;<a href="https://www.linkedin.com/in/atishpotdar/">Atish Potdar</a>.</p>\r\n\r\n<p><a href="http://www.gramcover.com/#!/up" rel="noopener" target="_blank"><strong>GramCove</strong>r</a>&nbsp;-&nbsp;Noida-based GramCover is an insurtech marketplace for rural India, which was founded by Jatin Singh and&nbsp;<a href="https://www.linkedin.com/in/gargrishabh/#experience-section" rel="noopener" target="_blank">Rishabh Garg</a>. The platform works with insurers to create affordable and easy-to-understand insurance products.</p>\r\n\r\n<p><strong><a href="http://www.jai-kisan.com/" rel="noopener" target="_blank">Jai Kisan</a></strong>&nbsp;-&nbsp;Jai Kisan was founded in 2017 by&nbsp;<a href="https://www.linkedin.com/in/arjun-ahluwalia-02baa325/" rel="noopener" target="_blank">Arjun Ahluwalia</a>&nbsp;and&nbsp;<a href="https://www.linkedin.com/in/adrielmaniego/" rel="noopener" target="_blank">Adriel Maniego</a>. It is&nbsp;an ecommerce platform that uses multiple data points to create a credit scoring for small farmers &mdash; enabling them an access to low-cost capital to purchase agri-input and types of equipment.</p>\r\n\r\n<p><strong><a href="http://www.kaarva.com/" rel="noopener" target="_blank">Kaarva</a></strong>&nbsp;-&nbsp;Based in Bengaluru, Kaarva offers instant small credit through its pay-what-you-like model to salaried professionals to meet their short term financial needs. It was founded by&nbsp;<a href="https://www.linkedin.com/in/maheshwarikhushboo/" rel="noopener" target="_blank">Khushboo Maheshwari</a>&nbsp;and&nbsp;<a href="https://www.linkedin.com/in/agamgoyal/" rel="noopener" target="_blank">Agam Goyal</a>.</p>\r\n\r\n<p><strong><a href="https://munshig.in/" rel="noopener" target="_blank">MunshiG</a></strong>&nbsp;-&nbsp;Based in Udaipur, MunshiG is a platform for small grocery stores that uses AI-based technologies to understand the behaviors of their customers, which is helpful in managing inventory and bookkeeping.</p>\r\n\r\n<p><strong>Navaana Tech</strong>&nbsp;-&nbsp;The platform uses voice-assisted and image-based friendly digital interfaces to improve the quality of life for individuals with low levels of literacy.</p>\r\n\r\n<p><strong><a href="https://sureclaim.in/about-us/" rel="noopener" target="_blank">SureClaim</a></strong>&nbsp;-&nbsp;Bengaluru-based SureClaim&nbsp;is an intelligent platform which helps process insurance claims. The platform was founded by&nbsp;<a href="https://www.linkedin.com/in/scriptaj/" rel="noopener" target="_blank">Anuj Jindal</a>&nbsp;and&nbsp;<a href="https://www.linkedin.com/in/varunkansalsureserve/" rel="noopener" target="_blank">Varun Kansal</a>&nbsp;in 2017.</p>\r\n\r\n<p><strong>WonderLend Hubs</strong>&nbsp;-&nbsp;WonderLend Hubs is focused on harnessing the power of big data, machine decisioning and human innovation, to make affordable finance easily accessible to the people. It&nbsp;is a digital platform that uses financial and social data to create credit profiles resulting in shortened turnaround times and lower rejection rates for&nbsp;Micro, Small and Medium Enterprises (MSMEs) and individual borrowers who lack sufficient credit history.</p>\r\n\r\n<p><strong><a href="https://bridge2capital.com/about-content/" rel="noopener" target="_blank">XtraCap</a></strong>&nbsp;-&nbsp;Delhi-based&nbsp;Xtracap Fintech which is registered under the domain of Bridge2capital&nbsp;uses GST (Goods &amp; Services Tax) data to assess the credit risk of small businesses, providing them access to short-term liquidity against supplier invoices.</p>\r\n\r\n<h2><strong>What&rsquo;s In Store For The Selected Startups?</strong></h2>\r\n\r\n<p>The startups will be trained to use technologies such as automation, underwriting and machine learning (ML) to assist them in their innovation that aids financial conditions of the people, especially from the lower and middle-income group.</p>\r\n\r\n<p>It is learnt that the initiative has drawn insights from&nbsp;Lucknow-based&nbsp;financial inclusion consulting firm MicroSave, which has concluded that over 40% of the Indian population have not used technology-enabled financial services.</p>\r\n\r\n<p>To bridge this gap, the first batch of the lab will be focusing on providing awareness, access to suitable products, and non-intuitive user interfaces to people through tech-enabled innovation, aimed at helping them adapt fintech services.</p>\r\n\r\n<p>For a country like India, where many are still contemplating on whether one should use fintech services &mdash; mainly fears of losing money and personal data due to lack of education &mdash; such initiatives are an important step towards getting people to understand the benefits of a tech-enabled financial ecosystem.</p>', 'news/images/Screenshot_6.png', 'https://inc42.com/buzz/meet-the-11-fintech-startups-from-the-financial-inclusion-lab/', 'Bridge2Capital', '2021-02-23 12:47:44.923646', '2021-02-23 12:47:44.923684', '2018-10-26', 0, 'meet-the-11-fintech-startups-from-the-financial-inclusion-lab', '', '', 'English'),
	(10, 'Eleven fintech startups selected for Financial Inclusion Lab program', 'By Gayatri Nayak, ET Bureau', 'The Financial Inclusion Lab, an initiative of IIM-Ahmedabad supported by JPMorgan and some philanthropic investors, has selected eleven fintech innovators for mentoring and upscaling their actvities and help deepen financial penetration in the country.', '<p>The Financial Inclusion Lab, an initiative of IIM-Ahmedabad supported by JPMorgan and some philanthropic investors, has selected eleven fintech innovators for mentoring and upscaling their actvities and help deepen financial penetration in the country<br />\r\n<br />\r\nThe selected firms will be mentored in use of technology-based strategies such as automation, underwriting and machine learning to help improve the financial health of lower- and middle-income (LMI) people, farmers and small entrepreneurs.</p>', 'news/images/Screenshot_10.png', 'https://economictimes.indiatimes.com/small-biz/startups/newsbuzz/eleven-fintech-startups-selected-for-financial-inclusion-lab-program/articleshow/66347545.cms?utm_source=contentofinterest&utm_medium=text&utm_campaign=cppst', 'Bridge2Capital', '2021-02-23 12:50:10.386854', '2021-02-23 12:50:10.386887', '2018-10-24', 0, 'eleven-fintech-startups-selected-for-financial-inclusion-lab-program', '', '', 'English');
